<?php

require_once "../inc/common.inc.php";


function insert_dash_connect_block($authUrl, $ga_connect, $ya_connect)
{
    ?>

  <script type="text/javascript">

    $(function () {
      $('#gaConnect').change(function () {
        if ($(this).prop('checked')) {
          window.location.href = '<?=$authUrl?>'
        } else {
          window.location.href = '/dash?ga_logout'
        }
        return false;
      })

      $('#ymConnect').change(function () {
        if ($(this).prop('checked')) {
          window.location.href = '/dash?ym_login'
        } else {
          window.location.href = '/dash?ym_logout'
        }
        return false;
      })
    })

  </script>

  <h2>Connect</h2>

  <form action="" class="form-inline">
    <div class="form-group col-sm-6">
      <label for="gaConnect" class=" control-label">

        <span class="ORR0NHB-l-b ORR0NHB-Y-h"></span>
        <span class="ORR0NHB-G-n ORR0NHB-l-a"></span>
      </label>
      <input id="gaConnect" class="connect-trigger" type="checkbox" <?= $ga_connect ? 'checked' : '' ?>
             data-toggle="toggle" data-onstyle="success" data-width="100"
             data-on="Connect" data-off="Not Ready" data-style="ios" data-size="mini">
    </div>

    <div class="form-group col-sm-6">
      <label for="ymConnect" class="control-label">
        <img class="image"
             src="data:image/svg+xml;base64,"
             alt="Яндекс">
        <img class="image"
             src="data:image/svg+xml;base64,"
             alt="">
      </label>
      <input id="ymConnect" class="connect-trigger" type="checkbox" <?= $ya_connect ? 'checked' : '' ?>
             data-toggle="toggle" data-onstyle="success" data-width="100"
             data-on="Connect" data-off="Not Ready" data-style="ios" data-size="mini">
    </div>

  </form>

    <?

}


